function getDefaultLang(cookie){var lang=cookie.getCookie();return(lang==="")?"RU":lang}
function translateContent(lang,dict){$("[translate-key]").each(function(){$(this).text(dict[lang][$(this).attr("translate-key")])})}
function translateMetaDescription(lang,dict){$("meta[name=description]").attr("content",dict[lang]["meta-description"])}
function setLang(lang,pageLangSelector){pageLangSelector.each(function(){if(($(this)[0].innerHTML)===lang){$(this).addClass("checked")}})}
